/** @format */

import React, { Component } from 'react';
import { Row, Col, Button } from 'react-bootstrap';

class Make_Order extends Component {
    constructor() {
        super();
        this.state = {
            elements: {},
            order: [],
            type: 'burger',
            quantity: 1,
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/api/manage/stock', {
            method: 'GET',
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .then((response) => response.json())
            .then((data) => this.setState({ elements: data.response }));
    }
    AddOrder = () => {
        const {type, quantity,order}=this.state
    };
    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };
    render() {
        const { elements } = this.state;
        return (
            <Row className='g-2'>
                <Row>
                    <Col>
                        <select name='type' onChange={this.handleChange}>
                            {Object.keys(elements).map((i) => {
                                return <option>{elements[i].type}</option>;
                            })}
                        </select>
                    </Col>
                    <Col md>
                        <input
                            name='quantity'
                            onChange={this.handleChange}
                            type='number'
                            min={1}
                            max={2}
                            placeholder='n >= 1, n <= 50'
                        />
                    </Col>
                    <Col md>
                        <Button variant='success' onClick={this.AddOrder}>
                            Agregar
                        </Button>
                    </Col>
                </Row>
                <Row></Row>
            </Row>
        );
    }
}
export default Make_Order;
