/** @format */

import React, { Component } from 'react';
import { Tabs, Tab, Container } from 'react-bootstrap';
import Order from './Order';

class Tabla extends Component {
    constructor(props) {
        super();
        this.state = {
            orders: [],
            pending: [],
            in_process: [],
            completed: [],
            delivered: [],
            canceled: [],
        };
    }
    componentDidMount() {
        this.check_orders();
        const { orders } = this.state;
        console.log(orders);
    }
    check_orders = async () => {
        await fetch('http://localhost:8080/api/transaction/get/all', {
            method: 'GET',
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .then((response) => response.json())
            .then((data) => {
                Object.keys(data.response).map((sku) => {
                    const { pending, in_process, completed, delivered, canceled } = this.state;
                    switch (data.response[sku].status) {
                        case 'Pending':
                            this.setState({ pending: [...pending, {sku,}]});
                            break;
                        case 'In Process':
                            this.setState({ in_process: [...in_process, ]});
                            break;
                        case 'Completed':
                            this.setState({ completed: [...completed, ]});
                            break;
                        case 'Delivered':
                            this.setState({ delivered: [...delivered, ]});
                            break;
                        case 'Canceled':
                            this.setState({ canceled: [...canceled, ] });
                            break;
                    }
                    this.setState({});
                    console.log(data.response[sku].orders);
                });
            });
    };
    ren;
    render() {
        const { pending, in_process, completed, delivered, canceled } = this.state;
        /*const pending = this.props.data.map((element) => element.status === 'Pending');
        const in_process = this.props.data.map((element) => element.status === 'In Process');
        const completed = this.props.data.map((element) => element.status === 'Completed');
        const delivered = this.props.data.map((element) => element.status === 'Delivered');
        const canceled = this.props.data.map((element) => element.status === 'Canceled');*/
        return (
            <Container fluid>
                <Tabs defaultActiveKey='profile' id='uncontrolled-tab-example' className='mb-3'>
                    <Tab eventKey='pending' title='Pending'>
                        {pending.map((element, idx) => {
                            return <Order id={idx} sku={element.sku} order={element.order} status={element.status} />;
                        })}
                    </Tab>
                    <Tab eventKey='in_process' title='In Process'>
                        {in_process.map((element, idx) => {
                            return <Order id={idx} sku={element.sku} order={element.order} status={element.status} />;
                        })}
                    </Tab>
                    <Tab eventKey='completed' title='Completed'>
                        {completed.map((element, idx) => {
                            return <Order id={idx} sku={element.sku} order={element.order} status={element.status} />;
                        })}
                    </Tab>
                    <Tab eventKey='delivered' title='Delivered'>
                        {delivered.map((element, idx) => {
                            return <Order id={idx} sku={element.sku} order={element.order} status={element.status} />;
                        })}
                    </Tab>
                    <Tab eventKey='canceled' title='Canceled'>
                        {canceled.map((element, idx) => {
                            return <Order id={idx} sku={element.sku} order={element.order} status={element.status} />;
                        })}
                    </Tab>
                </Tabs>
            </Container>
        );
    }
}
export default Tabla;
