/** @format */

import React, { Component } from 'react';
import { Card, Row, Col, Badge } from 'react-bootstrap';

class Make_Order extends Component {
    constructor() {
        super();
        this.state = {
            elements: {},
        };
    }
    componentDidMount() {
        fetch('http://localhost:8080/api/manage/stock', {
            method: 'GET',
            mode: 'cors', // no-cors, *cors, same-origin
            cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .then((response) => response.json())
            .then((data) => {
                const elements = Object.keys(data.response).map((value, idx) => {
                    return { type: [value].type, quantity: data.response[value].quantity };
                });
                this.setState({ elements: data.response });
            });
    }

    render() {
        const { elements } = this.state;
        return (
            <Card style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>
                        <Row>
                            <p as={Col}>
                                Order #000{this.props.sku}{' '}
                                <Badge as={Col} bg='secondary'>
                                    {this.props.status}
                                </Badge>
                            </p>
                        </Row>
                    </Card.Title>
                    <Card.Text></Card.Text>
                </Card.Body>
            </Card>
        );
    }
}
export default Make_Order;
